import React from 'react';
import './App.css';
import Page from './Components/Page';
import ListView from './Components/ListView';
import Header from './Components/Header';

export default class App extends React.PureComponent {
  constructor(props){
    super(props)
    this.state = {
      page: 'LandingPage',
      currentPlaylist: {},
      playlists: [
        {
          name: 'Example',
          songs: [
            {
              name: '3 nights by',
              artist: 'Dominic Fike'
            }, 
          ]
        }
      ],
      songNames: [
        {
          name: '3 nights by',
          artist: 'Dominic Fike'
        }, 
        {
          name: 'Bang!',
          artist: 'AJR',
        },
        {
          name: 'Tribe',
          artist: 'Jim Swim'
        },
        {
          name: 'Feels Right',
          artist: 'Danny\'s dead'
        },
        {
          name: 'Mariposa',
          artist: 'Peach Tree Rascals'
        },
        {
          name: 'Blinding Lights',
          artist: 'The Weeknd'
        },
        {
          name: 'Thriller',
          artist: 'Michael Jackson'
        },
        {
          name: 'Yellow Submarine',
          artist: 'The Beatles'
        },
        {
          name: 'USSR',
          artist: 'The Beatles'
        },
      ],
      librarySongNames: [
        {
          name: 'Mariposa',
          artist: 'Peach Tree Rascals'
        },
        {
          name: 'Blinding Lights',
          artist: 'The Weeknd'
        },
        {
          name: 'Thriller',
          artist: 'Michael Jackson'
        },
        {
          name: 'Yellow Submarine',
          artist: 'The Beatles'
        },
        {
          name: 'USSR',
          artist: 'The Beatles'
        },
      ],
    }
  }

  changeState = (newPage) => {
    console.log(newPage)
    this.setState({page: newPage})
  }

  removeFunc = (item, list) => {
    const temp = list.map((listItem) => {
      if(listItem !== item){
        return listItem
      }
    })
    console.log(temp)
  }

  render() {
    return (
      <div className="App">
        {this.state.page === 'LandingPage' ?
          <Page pageChanger = {this.changeState}/>
        : this.state.page === 'Playlists' ?
          <Page 
            pageChanger = {this.changeState} 
            contents = {
              <>
                <Header 
                  page = {this.state.page}
                  addButtonHandler = {() => {
                    const newPlaylist = {
                      name: 'New Playlist ' + this.state.playlists.length,
                      songs: []
                    }
                    var temp = this.state.playlists
                    temp.push(newPlaylist)
                    this.setState({page: 'Edit List', currentPlaylist: newPlaylist, playlists: temp})
                  }}
                />
                <ListView 
                  pageChanger = {this.changeState} 
                  removeOnClick = {(playlist) => this.removeFunc(playlist, this.state.playlists)}
                  data = {this.state.playlists}
                  onClick = {(currentPlaylist) => {
                    this.setState({page: 'Edit List', currentPlaylist})
                  }}
                />
              </>
            }
          />
        : this.state.page === 'Now Playing' ?
          <Page 
            pageChanger = {this.changeState}
          />
        : this.state.page === 'Search' ?
          <Page 
            pageChanger = {this.changeState} 
            contents = {
              <>
                <Header page = {this.state.page}/>
                <ListView 
                  pageChanger = {this.changeState} 
                  data = {this.state.songNames}
                  onClick = {(song) => {
                    var temp = this.state.currentPlaylist.songs ? this.state.currentPlaylist.songs : []
                    temp.push(song)
                    this.setState({currentPlaylist: {
                      ...this.state.currentPlaylist,
                      songs: temp
                    }})
                  }}
                />
              </>
            }
          />
        : this.state.page === 'Library' ?
          <Page 
            pageChanger = {this.changeState} 
            contents = {
              <>
                <Header page = {this.state.page}/>
                <ListView pageChanger = {this.changeState} data = {this.state.librarySongNames}/>
              </>
            }
          />
        : this.state.page === 'Edit List' && 
          <Page 
            pageChanger = {this.changeState} 
            contents = {
              <>
                <Header 
                  page = {this.state.page}
                  name = {this.state.currentPlaylist ? this.state.currentPlaylist.name : 'N/A'}
                  addButtonHandler = {() => {
                    this.setState({page: 'Search'})
                  }}
                />
                <ListView pageChanger = {this.changeState} data = {this.state.currentPlaylist.songs}/>
              </>
            }
          />
        }
      </div>
    )
  };
}
